describe("event aggregator",function(){describe("when triggering an event",function(){var e,t;beforeEach(function(){var e=new Wreqr.EventAggregator;e.on("foo",function(){t=!0}),e.trigger("foo")}),it("should fire handlers",function(){expect(t).toBe(!0)})}),describe("when unbinding from an event and then triggering it",function(){var e,t;beforeEach(function(){var e=new Wreqr.EventAggregator,n=function(){t=!0};binding=e.on("foo",n),e.off("foo",n),e.trigger("foo")}),it("should not fire any handlers",function(){expect(t).toBeUndefined()})})});