function printJSON(){$("#json").val(JSON.stringify(json))}function updateJSON(e){json=e,printJSON()}function showPath(e){$("#path").text(e)}var json={string:"foo",number:5,array:[1,2,3],object:{property:"value",subobj:{arr:["foo","ha"],numero:1}}};$(document).ready(function(){$("#rest > button").click(function(){var e=$("#rest-url").val();$.ajax({url:e,dataType:"jsonp",jsonp:$("#rest-callback").val(),success:function(e){json=e,$("#editor").jsonEditor(json,{change:updateJSON,propertyclick:showPath}),printJSON()},error:function(){alert("Something went wrong, double-check the URL and callback parameter.")}})}),$("#json").change(function(){var e=$("#json").val();if(e)try{json=JSON.parse(e)}catch(t){alert("Error in parsing json. "+t)}else json={};$("#editor").jsonEditor(json,{change:updateJSON,propertyclick:showPath})}),$("#expander").click(function(){var e=$("#editor");e.toggleClass("expanded"),$(this).text(e.hasClass("expanded")?"Collapse":"Expand all")}),printJSON(),$("#editor").jsonEditor(json,{change:updateJSON,propertyclick:showPath})});